package src.com.leveldown.main;import java.awt.Color;import java.awt.Font;import java.awt.Graphics;import java.awt.Graphics2D;import java.awt.RenderingHints;/*import java.io.BufferedReader;import java.io.BufferedWriter;import java.io.File;import java.io.FileNotFoundException;import java.io.FileReader;import java.io.FileWriter;import java.io.IOException;*/public class Menu {   public static String[] options = new String[]{"Novo jogo", "Carregar jogo", "Sair"};   public static int currentOpition = 0;   public static int maxOption;   private static int dx;   private static boolean animar;   public static boolean pause;   public boolean up;   public boolean down;   public boolean enter;   static {      maxOption = options.length - 1;      dx = 0;      animar = true;      pause = false;   }   public void tick() {      if (this.up) {         this.up = false;         --currentOpition;         if (currentOpition < 0) {            currentOpition = maxOption;         }      }      if (this.down) {         this.down = false;         ++currentOpition;         if (currentOpition > maxOption) {            currentOpition = 0;         }      }      if (this.enter) {         this.enter = false;         if (options[currentOpition] == "Sair") {               System.exit(1);         } else {            Game.gameState = "NORMAL";            pause = false;         }      }   }      public static void render(Graphics g) {      Graphics2D g2 = (Graphics2D)g;      g2.setRenderingHint(RenderingHints.KEY_TEXT_ANTIALIASING,     		  			  RenderingHints.VALUE_TEXT_ANTIALIAS_LCD_HRGB);      g2.setColor(new Color(0, 0, 0, 100));      g2.fillRect(0, 0, 720, 480);      g2.setFont(new Font("arial", 1, 24));      g.setColor(Color.black);      g.drawString("Resident Vitor", -222 + dx, 50);      g.setColor(Color.red);      g.drawString("Resident Vitor", -221 + dx, 49);      g.setColor(Color.red);      if (dx > 249) {         animar = false;      }      if (animar) {         dx += 3;      } else {         g2.setFont(new Font("arial", 1, 15));         g.setColor(Color.white);         if (pause) {            g.drawString("Voltar", 40, 80);         } else {            g.drawString(options[0], 40, 80);         }         g.drawString(options[1], 40, 100);         g.drawString(options[2], 40, 120);         if (options[currentOpition] == "Novo jogo") {            g.drawString(">", 23, 80);         } else if (options[currentOpition] == "Carregar jogo") {            g.drawString(">", 23, 100);         } else if (options[currentOpition] == "Sair") {            g.drawString(">", 23, 120);         }      }   }}